module.exports = GradeWith

var grader = require('Grade')

function GradeWith(data, timeslot, studentsInTimeslot, extraStudent) {
    var newStudentsArr = []
    for(i=0;i<studentsInTimeslot.length;i++){
        newStudentsArr.push(studentsInTimeslot[i])
    }
    newStudentsArr.push(extraStudent)
    console.log("With: " + studentsInTimeslot)
    var grade = grader(data,timeslot,newStudentsArr)
    return grade
}