module.exports = Parser;

function Parser(_input){
	
	this.input = _input.split(/\r?\n/);
    this.index = 0;
	
}

Parser.prototype.getLine = function(){
	
	var line;
	while(true) {
		
		line = this.input[this.index++];
        
        // if we haven't received a line, return 
        if (typeof line === "undefined") return;
		
		if (line && line.indexOf('#') !== 0)
			return line;
			
	}
};

Parser.prototype.getSecondPart = function(){ 

    var line = this.getLine();
    var index = line.indexOf(": ");
    
    return line.substring(index + 2);
};

Parser.prototype.getSecondPartAsNumber = function(){ 
	return Number(this.getSecondPart());
};
