module.exports = Parser;

function Parser(_input){
	
	this.input = _input.split('\n');
	
}

Parser.prototype.getLine = function(){
	
	var line;
	while(true) {
		
		line = this.input.shift().trim();
		
		if (line.indexOf('#') !== 0)
			return line;
			
	}
};

Parser.prototype.getSecondPart = function(){ 
	
    // splits based on ': ', and then takes the remaining parts after the first one
    // and joins them back together
	return this.getLine().split(/:\s+/).splice(1).join(" ");
};

Parser.prototype.getSecondPartAsNumber = function(){ 
	return Number(this.getSecondPart());
};
