/**
 * Used to process run time arguments
 * @type {{time, outputFile, swapType}}
 */
module.exports = (function(){
    
    // this object will become module.exports
    return {
        time: isNaN( +process.argv[2] ) ? 1000 : +process.argv[2] * 1000,
        outputFile: typeof process.argv[3] === 'undefined' ? "solution" : process.argv[3],
        swapType: typeof process.argv[4] === 'undefined' ? "regular" : process.argv[4]
    };
    
    
})();