module.exports = Parser;

var input;

function Parser(_input){
	input = _input.split('\n');
	
	var data = {};
	
	data.format = Number(getSecondPart());
	data.description = getSecondPart();
	data.groupSize = {};
	data.groupSize.min = getSecondPartAsNumber();
	data.groupSize.max = getSecondPartAsNumber();
	data.penalties = {};
	data.penalties.belowMin = getSecondPartAsNumber();
	data.penalties.aboveMax = getSecondPartAsNumber();
	data.penalties.possibleChoice = getSecondPartAsNumber();
	
	return data;
}

function getLine(){
	var line;
	while(true) {
		
		line = input.shift().trim();
		
		if (line.indexOf('#') !== 0)
			return line;
			
		}
}

function getSecondPart(){
	var line = getLine();
	return line.split(/:\s+/)[1];
}

function getSecondPartAsNumber(){
	var line = getLine();
	return Number(line.split(/:\s+/)[1]);
}
