module.exports = randomAssignment;

function randomAssignment(data){
    var assignment = {};
    var times = [];
    for(i=0;i<data.class.groups.length;i++){
        assignment[data.class.groups[i].time] = [];
        times.push(data.class.groups[i].time);
    }

    var maxGroupSize = Math.ceil(data.student.length/(times.length+1));

    for(i=0;i<data.student.length;i++) {
        while (true) {
            var ran = randNumber(0, times.length);
            if (assignment[times[ran]].length < maxGroupSize) {
                assignment[times[ran]].push(data.student[i].email);
                break;
            }
        }

    }
    return assignment;
}

function randNumber(min,max){
    return Math.floor(Math.random() * (max-min) + min);
}
