var tmp = [1,2,3,4,5,6,7]

function randomize(input) {
    var ret = []
    while (input.length > 0){
        console.log("in")
        var random = Math.floor(Math.random() * input.length)
        var currVal = input[random]
        ret.push(currVal)
        input.splice(random,1)
    }
    return ret;
}

tmp = randomize(tmp)

console.log(tmp)